import React from "react";
import styled from "styled-components";

const StyledButton = styled.button`
  //<button></button>버튼 설정
  padding: 8px 16px;
  font-size: 16px;
  border-width: 1px;
  border-radius: 8px;
  cursor: pointer;
`;

function Button(props) {
  const { title, onClick } = props; //title = props.title, onClick = props.onClick 비구조화 할당

  return <StyledButton onClick={onClick}>{title || "button"}</StyledButton>; //버튼 onClick 설정 children은 props.title 또는 "button"
  //클릭 이벤트를 상위 컴포넌트에서 받을 수 있도록 return을 함
}

export default Button;
