import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  width: calc(
    100% -32px
  ); //calc() 함수는 속성 값을 동적으로 결정 CSS 속성 값 내에서 더하기, 빼기, 곱하기 및 나누기와 같은 산술 연산을 수행
  //100% - 32px' 표현식은 상위 요소의 너비에서 '32px'를 빼서 해당 요소의 너비를 계산
  padding: 16px;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  border: 1px solid grey;
  border-radius: 8px;
  cursor: pointer; //속성은 웹 페이지의 요소 위로 마우스를 가져갈 때 커서의 모양을 변경(예: 손모양으로)
  background: white;
  :hover {
    background: lightgrey;
  } //cursor가 올라 갈 때 색깔 변경
`;

const ContentText = styled.p`
  font-size: 14px;
`;

function CommentListItem(props) {
  const { Comment } = props;
  return (
    <Wrapper>
      <ContentText>{Comment.content}</ContentText>
    </Wrapper>
  );
}

export default CommentListItem;
