import React from "react";
import styled from "styled-components";

const StyledTextarea = styled.textarea`
  //input값이 매우 많기 때문에 <textarea></textarea>로 설정함
  width: calc(100% -32px); //너비 100% 비율에서 32px를 빼기
  ${(props) =>
    props.height &&
    `
    height: ${props.height}px;
  `} //props로 받은 높이 설정
  padding: 16px;
  font-size: 16px;
  line-height: 20px;
`;

function TextInput(props) {
  const { height, value, onChange } = props; // props 비구조화 할당
  return <StyledTextarea height={height} value={value} onChange={onChange} />; //다른 함수에서 전달받은 props를 각각 이벤트로 설정
}

export default TextInput;
